(window.webpackJsonp=window.webpackJsonp||[]).push([[91],{471:function(t,s,a){"use strict";a.r(s);var r=a(0),e=Object(r.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h2",{attrs:{id:"_20250530"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#_20250530"}},[t._v("#")]),t._v(" 20250530")]),t._v(" "),s("p",[t._v("前段时间陆续阅读了印心同志写的有关毛选的解析，收获颇丰，从理解层面远超自己看原书时的感悟。更加具象化了毛选的思想精髓。\n毛选不是一本经书，而是成书于那段动荡的近代史中，讲解在面对一次又一次的困难，该如何面对，如何选择，如何行动的书，是一本行动指南。全书无一例外都贯穿了“实事求是”的思想精髓，并从这一思想中衍生出大量凝聚教员智慧的总结。")]),t._v(" "),s("h2",{attrs:{id:"_20230320"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#_20230320"}},[t._v("#")]),t._v(" 20230320")]),t._v(" "),s("p",[t._v("近期一口气将教员的选集从第一卷末尾看至第六卷，收获颇多，有很多的感想，想以此文抒发一下，也是对教员同志的怀念。")]),t._v(" "),s("p",[t._v("首先得澄清下阅读《毛泽东选集》的一些极大的误区。首先是很多人都听说过，毛选的特点就是通俗易懂，易于理解，不是那种故作玄虚的书。但实际上，对于现代读者的实际情况而言，很多人可能看了还是懵。这其中主要的原因是毛选的主要面向是广大的劳动人民，对于劳动人民，教员的表述是相当通俗易懂，深入人心的。但很多基于广大劳动人民生产生活实际情况的知识和观点对于还未踏足资本市场秩序的学生群体而言，是不容易理解的，这听起来可能是一件坏事，不过也是一件好事，因为如果学生能把这些精髓知识琢磨清除，参加工作后，就能在竞争激烈的就业市场中从容的看待很多问题。")])])}),[],!1,null,null,null);s.default=e.exports}}]);