(window.webpackJsonp=window.webpackJsonp||[]).push([[95],{475:function(v,_,p){"use strict";p.r(_);var t=p(0),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v('乌拉尔的夜晚比莫斯科更冷。沃罗宁藏身在斯维尔德洛夫斯克市郊的一间工人宿舍里，窗外的寒风呼啸着穿过工厂的烟囱，发出凄厉的呜咽。桌上摊开的是安德烈从当地图书馆"借"来的各种资料——经济报表、工厂生产数据、党的内部通报。')]),v._v(" "),_("p",[v._v("沃罗宁揉了揉酸痛的眼睛。三天来，他秘密会见了乌拉尔重型机械厂、下塔吉尔钢铁厂等十几个大型企业的工人代表。他们的热情让他欣慰，但问题也显而易见——三十年的官僚统治已经让普通工人丧失了政治主动性。他们习惯于等待上级指示，即使是最激进的工人代表，提出的要求也仅限于提高工资和改善劳动条件。\n"),v._v('\n"这不是革命意识，安德烈，"沃罗宁低声说，手指敲打着桌面，"这是工联主义。列宁在《怎么办？》中早就批判过这种局限性。"')]),v._v(" "),_("p",[v._v('安德烈正在角落里用一台老式打字机整理沃罗宁的讲话稿，闻言抬起头："但至少他们愿意听您讲话，米哈伊尔·伊万诺维奇。这已经是进步了。"')]),v._v(" "),_("p",[v._v("沃罗宁站起身，在狭小的房间里来回踱步。房间里的暖气时断时续，呼出的白气在面前凝结。他知道自己必须找到一种方法，一种能够真正唤醒工人阶级政治觉悟的方法，而不仅仅是带领他们进行经济斗争。")]),v._v(" "),_("p",[v._v('"我们需要一种新的形式，"他自言自语，"一种能够让群众自己教育自己、自己解放自己的方法..."')]),v._v(" "),_("p",[v._v('他的目光无意中扫过桌上的一叠发黄的报纸——安德烈从图书馆带回的"参考资料"，主要是西方媒体对社会主义国家的报道。其中一份《泰晤士报》的标题吸引了他的注意："中国\'文化运动\'十年回顾：乌托邦实验还是必要革命？"')]),v._v(" "),_("p",[v._v("沃罗宁精通英语，他拿起报纸开始阅读。文章充满了资产阶级的偏见和歪曲，但透过那些污蔑性的语言，沃罗宁隐约捕捉到了一些令他心跳加速的内容——群众自我教育、批判官僚主义、打破专业界限...")]),v._v(" "),_("p",[v._v('"安德烈！"他突然喊道，声音因激动而颤抖，"你还记得我们在列宁格勒时，那位老布尔什维克提到过中国的经验吗？"')]),v._v(" "),_("p",[v._v("安德烈困惑地眨眨眼：\"不太记得了，好像说过什么'中国人比我们更理解列宁'之类的话...\"")]),v._v(" "),_("p",[v._v("沃罗宁如饥似渴地翻阅着那堆资料，寻找任何关于中国的内容。大多数西方媒体的报道都充满敌意，但正是这种敌意让沃罗宁意识到那里可能发生过什么真正革命性的事情——足以让资产阶级恐惧的事情。")]),v._v(" "),_("p",[v._v('"我们需要更多关于中国的资料，"沃罗宁说，"特别是六十年代到七十年代的材料。"')]),v._v(" "),_("p",[v._v('"但这很难，米哈伊尔·伊万诺维奇，"安德烈面露难色，"官方图书馆里关于中国的书籍要么是早期歌颂中苏友谊的，要么是后来批判\'毛主义\'的..."')]),v._v(" "),_("p",[v._v('"那就去找旧书店，找私人收藏，"沃罗宁的眼睛闪烁着异样的光芒，"我必须了解那段历史。"')]),v._v(" "),_("p",[v._v('第二天傍晚，安德烈带回了一个褪色的帆布包，脸上带着胜利的喜悦："您绝对想不到我找到了什么！"')]),v._v(" "),_("p",[v._v("他从包里小心翼翼地取出几本装帧简陋的小册子，封面上的中文标题已经有些模糊，但还能辨认——《矛盾论》、《实践论》、《关于正确处理人民内部矛盾的问题》。")]),v._v(" "),_("p",[v._v('"斯维尔德洛夫斯克大学的一位汉学教授收藏的，"安德烈解释道，"他年轻时在中国留学。听说我是为\'高层研究\'需要，才勉强借给我的。"')]),v._v(" "),_("p",[v._v("沃罗宁如获至宝，立即开始阅读。尽管他的中文有限，但借助词典和安德烈找到的俄文译本，他很快抓住了这些著作的核心思想。")]),v._v(" "),_("p",[v._v("那一夜，沃罗宁没有合眼。随着阅读的深入，他感到一种前所未有的思想震撼。这里阐述的正是他苦苦寻找的答案——如何在无产阶级专政条件下继续革命，如何防止党和国家机器脱离群众，如何让人民群众真正成为历史的主人而非被动旁观者。")]),v._v(" "),_("p",[v._v('"这不是简单的\'运动\'，"黎明时分，沃罗宁对疲惫不堪的安德烈说，眼中布满血丝却闪烁着兴奋的光芒，"这是一整套关于社会主义条件下继续革命的理论！中国人早就解决了我们正在面对的问题！"')]),v._v(" "),_("p",[v._v('安德烈递给他一杯浓茶："但我们的官方立场一直批判这些理论和实践..."')]),v._v(" "),_("p",[v._v('"因为官僚们害怕被群众监督！"沃罗宁激动地站起身，"看这里——"他指着《关于正确处理人民内部矛盾的问题》中的一段话，"\'社会主义社会仍然存在矛盾，其中最主要的是生产关系与生产力之间的矛盾...这些矛盾必须通过群众的自我教育、自我解放来解决。\'这难道不正是苏联现在需要的吗？"')]),v._v(" "),_("p",[v._v('安德烈似懂非懂地点点头："那么您打算..."')]),v._v(" "),_("p",[v._v('沃罗宁开始在房间里快速踱步，思路越来越清晰："我们不能简单照搬中国的做法，但可以吸取其精髓——发动群众监督党和政府，打破官僚特权阶层对权力的垄断，让工人阶级直接参与国家管理..."')]),v._v(" "),_("p",[v._v('他突然停下脚步，转向安德烈："立即联系我们在莫斯科的支持者，告诉他们我有了新的想法。同时安排我与乌拉尔地区最大的几个工厂的工人委员会秘密会面——不是代表，而是整个委员会。"')]),v._v(" "),_("p",[v._v('"但这太危险了，米哈伊尔·伊万诺维奇！克格勃肯定已经在..."')]),v._v(" "),_("p",[v._v('"没有时间了，安德烈，"沃罗宁坚定地说，"如果我们要拯救社会主义，就必须采取大胆的行动。中国同志给了我们理论武器，现在必须将它运用到苏联的实际中去。"')]),v._v(" "),_("p",[v._v("三天后，在乌拉尔重型机械厂一个废弃的仓库里，沃罗宁面对着三十多名来自不同工厂的工人委员会成员。这些人大多是四五十岁的中年男子，脸上刻满岁月的痕迹和劳动的印记，眼神中混合着怀疑和期待。")]),v._v(" "),_("p",[v._v('"同志们，"沃罗宁开门见山，"你们认为苏联现在面临的最大问题是什么？"')]),v._v(" "),_("p",[v._v('一阵沉默后，一个满脸胡茬的锻工开口了："腐败。那些当官的把什么都偷光了。"')]),v._v(" "),_("p",[v._v('"物资短缺，"一个女工说，"我们的商店里什么都没有，而特权商店里堆满了进口货。"')]),v._v(" "),_("p",[v._v('"没有发言权，"年轻些的技工补充道，"工会成了摆设，我们的意见从来不被听取。"')]),v._v(" "),_("p",[v._v('沃罗宁点点头："那么你们认为，为什么会这样？"')]),v._v(" "),_("p",[v._v('这次回答来得更快："因为党脱离了群众！""因为官僚们只顾自己！""因为没有人监督他们！"')]),v._v(" "),_("p",[v._v('"正是如此！"沃罗宁提高了声音，"那么解决方案是什么？更多的法律？更多的检查？还是——"他停顿了一下，"让工人阶级直接行使监督权？"')]),v._v(" "),_("p",[v._v("仓库里安静下来，所有人都盯着他。")]),v._v(" "),_("p",[v._v('"我提议，"沃罗宁继续说，"在每个工厂建立\'工人监督委员会\'，不是咨询机构，而是拥有实权的机构，有权审查工厂所有账目，质询任何管理人员，甚至罢免不称职的厂长。"')]),v._v(" "),_("p",[v._v("工人们面面相觑，有人开始小声议论。")]),v._v(" "),_("p",[v._v('"但这...这不符合现行体制..."一个年长的工人犹豫地说。')]),v._v(" "),_("p",[v._v('"现行体制正在毁灭社会主义！"沃罗宁坚定地反驳，"列宁在1917年就说过，工人监督是\'走向社会主义的第一步\'。我们现在需要的是回到这个原则。"')]),v._v(" "),_("p",[v._v('他从公文包中取出几页纸："这是我起草的《工人监督条例》草案。它不是最终版本，需要你们——真正的工人阶级代表——来完善它。"')]),v._v(" "),_("p",[v._v("工人们传阅着文件，议论声越来越大。沃罗宁看到有人点头，有人皱眉，但没有人表现出冷漠或拒绝。")]),v._v(" "),_("p",[v._v('"如果上级不同意怎么办？"有人问。')]),v._v(" "),_("p",[v._v('"那么我们就从能够做到的地方开始，"沃罗宁回答，"哪怕只是一个车间，一个部门。重要的是让工人阶级重新掌握主动权，而不是被动等待官僚们的恩赐。"')]),v._v(" "),_("p",[v._v("会议持续到深夜。当工人们最终离开时，许多人眼中闪烁着沃罗宁久违的光芒——那不是乞求施舍的卑微，而是掌握自己命运的坚定。")]),v._v(" "),_("p",[v._v('"我们成功了，安德烈，"回到藏身处后，沃罗宁难掩兴奋，"这才是真正的社会主义民主的开端。"')]),v._v(" "),_("p",[v._v('安德烈却忧心忡忡："米哈伊尔·伊万诺维奇，刚刚收到消息，克格勃已经派特别小组来乌拉尔了。科尔扎科夫亲自带队。"')]),v._v(" "),_("p",[v._v('沃罗宁的表情变得严肃："我们还有多少时间？"')]),v._v(" "),_("p",[v._v('"最多两天。我们必须转移了。"')]),v._v(" "),_("p",[v._v('沃罗宁沉思片刻，突然笑了："不，安德烈。我们不仅不躲，还要主动出击。安排我明天在乌拉尔机械厂的工人集会上的讲话。如果克格勃想抓我，就让他们在全厂工人面前抓吧。"')]),v._v(" "),_("p",[v._v('"但这太冒险了！"')]),v._v(" "),_("p",[v._v('"革命从来就不是安全的事情，安德烈，"沃罗宁平静地说，"如果我的被捕能够唤醒更多工人的觉悟，那么这个代价是值得的。"')]),v._v(" "),_("p",[v._v('第二天早晨，乌拉尔机械厂的广播突然中断了日常的音乐节目，一个低沉的声音宣布："同志们，请于午休时间到中央广场集合，有重要消息宣布。"')]),v._v(" "),_("p",[v._v("与此同时，斯维尔德洛夫斯克火车站，一队穿着便装但行动整齐的男子走下火车。为首的正是科尔扎科夫将军，他冷峻的面容上看不出任何表情。")]),v._v(" "),_("p",[v._v('"目标在哪里？"他简短地问道。')]),v._v(" "),_("p",[v._v('"据线报，今天中午会在乌拉尔机械厂公开露面，"一个下属回答，"他似乎...似乎不打算隐藏了。"')]),v._v(" "),_("p",[v._v('科尔扎科夫微微皱眉："愚蠢的理想主义者。准备行动，但要等他的讲话结束后再动手。我想听听他到底要说什么。"')]),v._v(" "),_("p",[v._v("正午时分，乌拉尔机械厂的中央广场聚集了数千名工人。当沃罗宁走上临时搭建的讲台时，人群中爆发出一阵惊讶的议论声。许多人认出了这位经常在电视上出现的政府高官。")]),v._v(" "),_("p",[v._v('"同志们！"沃罗宁的声音通过扩音器传遍广场，"我不是以部长会议副主席的身份站在这里，而是以一个普通共产党员的身份，一个和你们一样深爱着社会主义祖国的劳动者的身份！"')]),v._v(" "),_("p",[v._v("广场安静下来，所有人都屏息听着。")]),v._v(" "),_("p",[v._v('"三十年来，我们建造了世界上最强大的工业体系，将人类送上了太空，却忘记了社会主义最根本的原则——工人阶级当家作主！"')]),v._v(" "),_("p",[v._v("人群中开始有人点头，有人小声附和。")]),v._v(" "),_("p",[v._v('"现在，有人告诉我们，解决危机的办法是引入资本主义元素，是向市场低头。但这是谎言！真正的解决办法是回归社会主义的本源——让工人农民直接参与国家管理，让群众监督党和政府的工作！"')]),v._v(" "),_("p",[v._v("掌声开始响起，起初零星，然后越来越热烈。")]),v._v(" "),_("p",[v._v('"为此，我提议在每一个工厂、每一个集体农庄建立工人监督委员会，让劳动人民真正成为国家的主人，而不是官僚主义的奴隶！"')]),v._v(" "),_("p",[v._v("欢呼声如雷鸣般响起。沃罗宁看到前排的工人们眼中闪烁着泪光，高举的拳头如森林般密集。")]),v._v(" "),_("p",[v._v("就在这一刻，他注意到广场边缘的骚动——一队穿制服的人正试图挤过人群。科尔扎科夫站在最前面，面色阴沉如水。")]),v._v(" "),_("p",[v._v('沃罗宁深吸一口气，决定利用最后的时刻："同志们！真正的社会主义不是来自上面的恩赐，而是靠我们自己的双手去争取！无论发生什么，请记住——工人阶级的力量在于团结和组织！社会主义的未来在你们手中！"')]),v._v(" "),_("p",[v._v("当克格勃的特工终于挤到讲台前时，整个广场的工人已经齐声高唱《国际歌》。科尔扎科夫出示了逮捕令，但在数千双愤怒的眼睛注视下，他不得不表现得相对克制。")]),v._v(" "),_("p",[v._v('"米哈伊尔·伊万诺维奇·沃罗宁，你因煽动社会动荡和违反党的纪律被逮捕了。"')]),v._v(" "),_("p",[v._v('沃罗宁平静地伸出双手："我只是履行了一个共产党员的义务，将军同志。"')]),v._v(" "),_("p",[v._v('当他被带离广场时，身后响起了震耳欲聋的呼声："放开沃罗宁！""我们要工人监督！""社会主义万岁！"')]),v._v(" "),_("p",[v._v('科尔扎科夫脸色铁青，低声对沃罗宁说："你知不知道你做了什么？你点燃了无法控制的火！"')]),v._v(" "),_("p",[v._v('沃罗宁回头看了一眼沸腾的工人海洋，嘴角浮现出一丝微笑："不，将军同志。我只是给了火花一个燃烧的机会。火种一直在工人阶级心中，从未熄灭。"')]),v._v(" "),_("p",[v._v("在被押上黑色伏尔加的最后一刻，沃罗宁看到安德烈在人群中向他点头示意——那是一个承诺的眼神。他知道，无论自己命运如何，这场由工人阶级自己发起的社会主义复兴运动已经无法阻挡了。")]),v._v(" "),_("p",[v._v("乌拉尔的天空下，《国际歌》的旋律久久回荡。")])])}),[],!1,null,null,null);_.default=n.exports}}]);